var buildReport = require("./lib/report.builder.js");
if (process.argv[1] === __filename){
    var Path = require("path");
    var outputFolder = process.argv[2];
    if (!outputFolder) {
        console.log("Output folder is null or empty");
        process.exit(1);
    }
    var xmlPath = Path.join(outputFolder, "runresults.xml");
    var jsonPath = Path.join(outputFolder, "runresults.json");
    var cssPath = Path.join(outputFolder, "runresults.css");
    var htmlPath = Path.join(outputFolder, "runresults.html");
    buildReport({xmlPath : xmlPath ,jsonPath : jsonPath , cssPath : cssPath, htmlPath : htmlPath });
}
else{
    module.exports = buildReport;
}




