var LFT = require("leanft.sdk.core");
var Utils = LFT.ObjectUtils;
var RunUnitNodeBase = require("./run_unit_node_base.js").RunUnitNodeBase;
var Enums = require("./enums.js");

function RunUnitOperationNode(node){
    this.init(node);
    this._nodeType = Enums.RunUnitNodeType.Step;
    this._isNestingAllowed = true;
    this._objectType = node.objectType;
    this._operation = node.operation;
    this._properties = node.properties || null;
    this._technology = node.technology;
    this._objectPath = node.objectPath || null;
    this._testObject = node.testObject || null;
    this._parameters = node.parameters || null;
    this._result = node.result || null;
    this._externalReportLink = node.externalReportLink || null;
    this._wiredName = node.wiredName || null;
}

Utils.inherit(RunUnitOperationNode, RunUnitNodeBase, {
    _objectType: null,
    _operation: null,
    _technology: null,
    _nativeType: null,
    _properties: null,
    _objectPath: null,
    _appModelData: null,
    _testObject: null,
    _parameters: null,
    _result: null,
    _externalReportLink: null,
    _wiredName: null
});

module.exports.RunUnitOperationNode = RunUnitOperationNode;



