var LFT = require("leanft.sdk.core");
var Utils = LFT.ObjectUtils;
var RunUnitNodeBase = require("./run_unit_node_base.js").RunUnitNodeBase;
var Enums = require("./enums.js");

function RunUnitVerificationNode(node){
    this.init(node);
    this._nodeType = Enums.RunUnitNodeType.Verify;
    this._isNestingAllowed = true;
    this._operation = node.operation || null;
    this._parameters = node.parameters || null;
    this._verifyDesc = node.verifyDesc || null;
}

Utils.inherit(RunUnitVerificationNode, RunUnitNodeBase, {
    _operation: null,
    _parameters: null,
    _verifyDesc: null
});

module.exports.RunUnitVerificationNode = RunUnitVerificationNode;



