var assert = require("assert");
var sinon = require("sinon");
var defaultReporterBehaviorUnderTest = require("../lib/default_reporter_behavior.js");
var emptyReporterBehaviorUnderTest = require("../lib/empty_reporter_behavior.js");

describe("Report behaviors", function(){

    it("should have the same number of functions", function(){
        assert.equal(Object.keys(emptyReporterBehaviorUnderTest).length, Object.keys(defaultReporterBehaviorUnderTest).length);
    });

    it("should implement the same functions", function(){
        defaultReporterBehaviorKeys = Object.keys(defaultReporterBehaviorUnderTest).sort();
        emptyReporterBehaviorKeys = Object.keys(emptyReporterBehaviorUnderTest).sort();

        for(var i = 0; i < defaultReporterBehaviorKeys.length; i++){
            assert.equal(emptyReporterBehaviorKeys[i], defaultReporterBehaviorKeys[i]);
        }
    });

});