var ArrayUtils = require("./object_utils.js").ArrayUtils;

/**
 * The behaviors of the Menu helper
 * @ignore
 */
var MenuHelperBehavior = {
    _helpers: {
        _firstItemIntValue: 1,
        /*jshint eqnull:true */
        /**
         * Create the path of the specified menu item.
         * @param {...(number|string)} menuPathParts The menu path parts can be: logical name(string), full path(string), or numeric index(int).
         * @returns {string} the path of the specified menu item.
         * @ignore
         */
        _buildMenuPath: function (menuPathParts) {
            if (menuPathParts == null)
                throw new Error("The parameter menuPathParts cannot be null.");
            menuPathParts = ArrayUtils.toArray(arguments);
            var items = menuPathParts.map((function (item) {
                if (typeof item === "string") {
                    return item;
                }
                else if (typeof item === "number") {
                    if (item >= this._firstItemIntValue) {
                        return this._convertFromIntToString(item);
                    }
                    else {
                        throw new Error("Illegal menu path int value (value must be >= " + this._firstItemIntValue + ")");
                    }
                }
                else {
                    throw new Error("Parameter type is not a string or an int");
                }
            }).bind(this));

            return items.join(";");
        },

        /**
         * @ignore
         */
        _convertFromIntToString: function (item) {
            return "<Item " + item + ">";
        }
    }
};

module.exports.MenuHelperBehavior = MenuHelperBehavior;
