var StackManipulator = {
    /**
     * Returns the stack string after removing the nofLinesToRemove lines from the top of the stack.
     * @function
     * @param {Object} stack The full stack from which to remove the lines.
     * @param {number} nofLinesToRemove The number of lines to remove from the top of the stack.
     * @returns {string} the stack string after removing of nofLinesToRemove lines from the top of the stack.
     */
  removeFromTop: function(stack, nofLinesToRemove) {
      var parts = stack.split("\n");

      var header = parts.splice(0,1);

      nofLinesToRemove = Math.min(nofLinesToRemove, parts.length);

      parts.splice(0,nofLinesToRemove);

      parts = header.concat(parts);

      return parts.join("\n");
  }
};


module.exports.StackManipulator = StackManipulator;
