/**
 * Possible values for the 'SwipeDirection' type.
 * @typedef {SwipeDirection} SwipeDirection
 * @property {string} left Swipes left.
 * @property {string} right Swipes right.
 * @property {string} up Swipes up.
 * @property {string} down Swipes down.
 */
var SwipeDirection = {
    /**
     * Swipes left.
     */
    left: "left",
    /**
     * Swipes right.
     */
    right: "right",
    /**
     * Swipes up.
     */
    up: "up",
    /**
     * Swipes down.
     */
    down: "down",

    /**
     * @private
     * @ignore
     */
    _convertToRuntime: {
        left: "Left",
        right: "Right",
        up: "Up",
        down: "Down"
    },

    /**
     * @ignore
     * @param direction
     * @returns {number}
     * @private
     */
    _convertForWeb: function (direction) {
        var webDirections = {
            left: 0,
            right: 1,
            up: 2,
            down: 3
        };
        return webDirections[direction.toLowerCase()];
    },

    /**
     * @ignore
     * @param direction
     * @returns {*}
     * @private
     */
    _convert: function(direction) {
        return SwipeDirection._convertToRuntime[direction.toLowerCase()];
    }
};

module.exports.SwipeDirection = SwipeDirection;