var StringUtils = require("./object_utils.js").StringUtils;
var Convert = require("./convert.js").Convert;

/**
 * TabControlDescriptionBehavior - the description for a TabControl object
 * This base class is expected to be used by StdWin and WinForms
 * @ignore
 */
var TabControlDescriptionBehavior = {
    /**
     * All tabs in the TabControl.
     * @param {string[]} [tabs] When provided, this method acts as a setter and sets the tabs value.
     * @returns {string[] | Description} If no parameter is provided, the method acts as a getter and returns the current stored tabs value;
     * otherwise it returns 'this' for use with the builder pattern.
     */
    tabs: function (tabs) {
        return this._propertyHandler("all items", tabs, StringUtils.joinFunc("\n"), StringUtils.splitFunc("\n"));
    },

    /**
     * The selected tab in the TabControl.
     * @param {string} [selectedItem] When provided, this method acts as a setter and sets the selectedTab value.
     * @returns {string | Description} If no parameter is provided, the method acts as a getter and returns the current stored selectedTab value;
     * otherwise it returns 'this' for use with the builder pattern.
     */
    selectedTab: function (selectedTab) {
        return this._simplePropertyHandler("selection", selectedTab);
    },

    /**
     * The number of tabs in the TabControl.
     * @param {number} [tabsCount] When provided, this method acts as a setter and sets the tabsCount value.
     * @returns {number | Description} If no parameter is provided, the method acts as a getter and returns the current stored tabsCount value;
     * otherwise it returns 'this' for use with the builder pattern.
     */
    tabsCount: function (tabsCount) {
        return this._propertyHandler("items count", tabsCount, Convert.toNumber);
    }
};

module.exports.TabControlDescriptionBehavior = TabControlDescriptionBehavior;
