var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;

/**
 * The description of a Java Applet test object.
 * @param {Object} [props] Initializes a Java Applet test object using the supplied property bag.
 * @returns {Applet} the description object that was initialized.
 * @class Applet
 * @extends Java.UiObjectBaseDescription
 * @memberof Java
 */
function Applet(props) {
    var appletDesc = new UiObjectBaseDescription("Applet");
    appletDesc._defineByPropertyBag(props);
    return appletDesc;
}

/**
 * A Java Applet test object
 * @constructor
 * @class AppletTO
 * @extends Java.UiObjectBaseTO 
 * @memberof Java
 */
function AppletTO(session, description) {
    description = description || Applet();
    var appletTO = new UiObjectBaseTO("JavaApplet", session, description);
    return appletTO;
}

module.exports.Applet = Applet;
module.exports.AppletTO = AppletTO;