var Core = require("leanft.sdk.core");
var ObjUtils = Core.ObjectUtils;
var WindowObjectBaseBehavior = Core.WindowObjectBaseBehavior;
var WindowObjectBaseExtendedBehavior = Core.WindowObjectBaseExtendedBehavior;
var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;
var WindowPackage = require("./window.js");
var WindowDescriptionBehavior = WindowPackage.WindowDescriptionBehavior;
var WindowTOBehavior = WindowPackage.WindowTOBehavior;

/**
 * The description of a Java Dialog test object.
 * @param {Object} [props] Initializes a Java Dialo test object using the supplied property bag.
 * @returns {Dialog} the description object that was initialized.
 * @class Dialog
 * @extends Java.UiObjectBaseDescription
 * @mixes Java.WindowDescriptionBehavior
 * @memberof Java
 */
function Dialog(props) {
    var dialogDesc = new UiObjectBaseDescription("Dialog");
    ObjUtils.extend(dialogDesc, WindowDescriptionBehavior);
    dialogDesc._defineByPropertyBag(props);
    return dialogDesc;
}

/**
 * A Java Dialog test object
 * @constructor
 * @class DialogTO
 * @extends Java.UiObjectBaseTO
 * @mixes WindowObjectBaseBehavior._methods
 * @mixes WindowObjectBaseExtendedBehavior._properties
 * @mixes WindowObjectBaseExtendedBehavior._methods
 * @mixes Java.WindowTOBehavior._methods
 * @mixes Java.WindowTOBehavior._properties
 * @memberof Java
 */
function DialogTO(session, description) {
    description = description || Dialog();
    var dialogTO = new UiObjectBaseTO("JavaDialog", session, description);
    dialogTO._mergeBehavior(WindowObjectBaseBehavior);
    dialogTO._mergeBehavior(WindowObjectBaseExtendedBehavior);
    dialogTO._mergeBehavior(WindowTOBehavior);
    return dialogTO;
}

module.exports.Dialog = Dialog;
module.exports.DialogTO = DialogTO;
