var Core = require("leanft.sdk.core");
var ObjUtils = Core.ObjectUtils;
var WindowBaseBehavior = Core.WindowObjectBaseBehavior;
var WindowObjectBaseExtendedBehavior = Core.WindowObjectBaseExtendedBehavior;
var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;
var WindowPackage = require("./window.js");
var WindowDescriptionBehavior = WindowPackage.WindowDescriptionBehavior;
var WindowTOBehavior = WindowPackage.WindowTOBehavior;

/**
 * The description of a Java InternalFrame test object.
 * @param {Object} [props] Initializes a Java Dialo test object using the supplied property bag.
 * @returns {InternalFrame} the description object that was initialized.
 * @class InternalFrame
 * @extends Java.UiObjectBaseDescription
 * @mixes Java.WindowDescriptionBehavior
 * @memberof Java
 */
function InternalFrame(props) {
    var internalFrameDesc = new UiObjectBaseDescription("InternalFrame");
    ObjUtils.extend(internalFrameDesc, WindowDescriptionBehavior);
    internalFrameDesc._defineByPropertyBag(props);
    return internalFrameDesc;
}

/**
 * A Java InternalFrame test object
 * @constructor
 * @class InternalFrameTO
 * @extends Java.UiObjectBaseTO
 * @mixes WindowBaseBehavior._methods
 * @mixes WindowObjectBaseExtendedBehavior._methods
 * @mixes WindowObjectBaseExtendedBehavior._properties
 * @mixes Java.WindowTOBehavior._methods
 * @mixes Java.WindowTOBehavior._properties
 * @memberof Java
 */
function InternalFrameTO(session, description) {
    description = description || InternalFrame();
    var internalFrameTO = new UiObjectBaseTO("JavaInternalFrame", session, description);
    internalFrameTO._mergeBehavior(WindowBaseBehavior);
    internalFrameTO._mergeBehavior(WindowObjectBaseExtendedBehavior);
    internalFrameTO._mergeBehavior(WindowTOBehavior);
    return internalFrameTO;
}

module.exports.InternalFrame = InternalFrame;
module.exports.InternalFrameTO = InternalFrameTO;