var Core = require("leanft.sdk.core");
var Convert = Core.Convert;
var StringUtils = Core.StringUtils;


/**
 * The base description for Java TreeView
 * @memberof Java
 */
var TreeViewBaseDescriptionBehavior = {
    /**
     * The number of columns in the tree.
     * @param {number} [columnsCount] When provided, this method acts as a setter and sets the columnsCount value.
     * @returns {number | Description} If no parameter is provided, the method acts as a getter and returns the current stored columnsCount value;
     * otherwise it returns 'this' for use with the builder pattern.
     */
    columnsCount: function (columnsCount) {
        return this._propertyHandler("columns_count", columnsCount, Convert.toNumber);
    },

    /**
     * The selected nodes in the tree.
     * @param {string[]} [selectedNodes] When provided, this method acts as a setter and sets the selectedNodes value.
     * @returns {string[] | Description} If no parameter is provided, the method acts as a getter and returns the current stored selectedNodes value;
     * otherwise it returns 'this' for use with the builder pattern.
     */
    selectedNodes: function (selectedNodes) {
        return this._propertyHandler("value", selectedNodes, StringUtils.joinFunc("\u0018"), StringUtils.splitFunc("\u0018"));
    }
};


module.exports.TreeViewBaseDescriptionBehavior = TreeViewBaseDescriptionBehavior;


