var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;
var ObjUtils = require("leanft.sdk.core").ObjectUtils;

/**
 * UiObjectDescriptionBehavior - the description for a Java UiObject
 * @memberof Java
 * @ignore
 */
var UiObjectDescriptionBehavior = {
     /**
     * The text displayed for the object.
     * @param {string} [text] When provided, this method acts as a setter and sets the text value.
     * @returns {string | Description} If no parameter is provided, the method acts as a getter and returns the current stored text value;
     * otherwise it returns 'this' for use with the builder pattern.
     * @memberof Java.UiObject#
     */
    text: function (text) {
        return this._simplePropertyHandler("text", text);
    }
};

/**
 * The description of a JavaObject test object.
 * @param {Object} [props] Initializes a JavaObject test object using the supplied property bag.
 * @returns {UiObject} the description object that was initialized.
 * @class UiObject
 * @extends Java.UiObjectBaseDescription
 * @mixes Java.UiObjectDescriptionBehavior
 * @memberof Java 
 */
function UiObject(props) {
    var uiObjectDesc = new UiObjectBaseDescription("UiObject");
    ObjUtils.extend(uiObjectDesc, UiObjectDescriptionBehavior);
    uiObjectDesc._defineByPropertyBag(props);
    return uiObjectDesc;
}

/**
* The behaviors of UiObject
* @memberof Java
* @ignore
*/
var UiObjectTOBehavior = {
    /**
     * @ignore
     * @memberof Java.UiObjectTOBehavior
     */
  _properties: {
      /**
       * Returns the text displayed for the object.
       * @function
       * @returns {Promise<string>} a promise that is fulfilled with the text displayed for the object.
       * @memberof Java.UiObjectTO#
       */
      text: {
      }
  }
};


/**
 * A JavaObject test object
 * @constructor
 * @class UiObjectTO
 * @extends Java.UiObjectBaseTO
 * @mixes Java.UiObjectTOBehavior._properties
 * @memberof Java 
 */
function UiObjectTO(session, description) {
    description = description || UiObject();
    var uiObjectTO = new UiObjectBaseTO("JavaObject", session, description);
    uiObjectTO._mergeBehavior(UiObjectTOBehavior);
    return uiObjectTO;
}

module.exports.UiObject = UiObject;
module.exports.UiObjectTO = UiObjectTO;