var UiObjectBase = require("./uiobject_base").UiObjectBaseTestObject;
var UiObjectBaseDesc = require("./uiobject_base").UiObjectBaseDescription;

/**
 * The description of a Button test object in a mobile application.
 * @param {Object} [props] Initializes the description using the supplied property bag.
 * @returns {Button} the description object that was initialized.
 * @constructor
 * @memberof Mobile
 * @extends Mobile.UiObjectBaseDescription
 */
function Button(props) {
    var buttonDes = new UiObjectBaseDesc("Button");

    buttonDes._defineByPropertyBag(props);

    return buttonDes;
}

/**
 * A button object in a mobile application.
 * @class ButtonTO
 * @memberof Mobile
 * @constructor
 * @extends Mobile.UiObjectBaseTestObject
 */
function ButtonTO(session, description) {
    description = description || Button();
    var buttonTO = new UiObjectBase("MobileButton", session, description);

    return buttonTO;
}

module.exports.Button = Button;
module.exports.ButtonTO = ButtonTO;