var UiObjectBase = require("./uiobject_base").UiObjectBaseTestObject;
var UiObjectBaseDesc = require("./uiobject_base").UiObjectBaseDescription;

/**
 * The description of a Label test object in a mobile application.
 * @param {Object} [props] Initializes the description using the supplied property bag.
 * @returns {Label} the description object that was initialized.
 * @constructor
 * @memberof Mobile
 * @extends Mobile.UiObjectBaseDescription
 */
function Label(props) {
    var labelDes = new UiObjectBaseDesc("Label");

    labelDes._defineByPropertyBag(props);

    return labelDes;
}

/**
 * A label in a mobile application.
 * @class LabelTO
 * @memberof Mobile
 * @constructor
 * @extends Mobile.UiObjectBaseTestObject
 */
function LabelTO(session, description) {
    description = description || Label();
    var labelTO = new UiObjectBase("MobileLabel", session, description);

    return labelTO;
}

module.exports.Label = Label;
module.exports.LabelTO = LabelTO;
