var Core = require("leanft.sdk.core");
var Logger = Core.Logger;
var ObjectUtils = Core.ObjectUtils;
var ConcreteFactoryBehavior = Core.ConcreteFactoryBehavior;

var DeviceTO = require("./device.js").DeviceTO;
var ApplicationTO = require("./application.js").ApplicationTO;
var UiObjectTO = require("./uiobject.js").UiObjectTO;
var ButtonTO = require("./button.js").ButtonTO;
var CalendarTO = require("./calendar.js").CalendarTO;
var LabelTO = require("./label.js").LabelTO;
var CheckBoxTO = require("./check_box.js").CheckBoxTO;
var EditFieldTO = require("./edit_field.js").EditFieldTO;
var RadioGroupTO = require("./radio_group.js").RadioGroupTO;
var SliderTO = require("./slider.js").SliderTO;
var StepperTO = require("./stepper.js").StepperTO;
var PaginatorTO = require("./paginator.js").PaginatorTO;
var DropDownTO = require("./drop_down.js").DropDownTO;
var TableTO = require("./table.js").TableTO;
var TabStripTO = require("./tab_strip.js").TabStripTO;
var ToggleTO = require("./toggle.js").ToggleTO;
var ViewTO = require("./view.js").ViewTO;
var WebViewTO = require("./web_view.js").WebViewTO;
var RecyclerViewTO = require("./recycler_view.js").RecyclerViewTO;

function MobileFactory(session){
    this._logger = new Logger("Mobile.TestObjectFactory");
    this._session = session;
    this._technology = "Mobile";

    ObjectUtils.extend(this, ConcreteFactoryBehavior);
}

MobileFactory.prototype = {
    _logger: null,
    _session: null,
    _technology: null,

    _constructors: {
        "device": DeviceTO,
        "application": ApplicationTO,
        "uiobject": UiObjectTO,
        "button": ButtonTO,
        "calendar": CalendarTO,
        "checkbox": CheckBoxTO,
        "label": LabelTO,
        "editfield": EditFieldTO,
        "radiogroup": RadioGroupTO,
        "slider": SliderTO,
        "stepper": StepperTO,
        "paginator": PaginatorTO,
        "dropdown": DropDownTO,
        "table": TableTO,
        "tabstrip": TabStripTO,
        "toggle": ToggleTO,
        "view": ViewTO,
        "webview": WebViewTO,
        "recyclerview": RecyclerViewTO
    },

    _nativeClassToLeanFtType: {
        "device": "device",
	    "app": "application",
        "mobileobject": "uiobject",
        "mobilebutton": "button",
        "mobilecalendar": "calendar",
        "mobilecheckbox": "checkbox",
	    "mobilelabel": "label",
        "mobileedit": "editfield",
        "mobileradiogroup": "radiogroup",
        "mobileslider": "slider",
        "mobilestepper": "stepper",
        "mobilepager": "paginator",
        "mobiledropdown": "dropdown",
        "mobilelist": "table",
        "mobiletabstrip": "tabstrip",
        "mobiletoggle": "toggle",
        "mobileview": "view",
        "mobilewebview": "webview",
        "mobilerecyclerview": "recyclerview"
    }
};

module.exports.MobileFactory = MobileFactory;