var ObjUtils = require("leanft.sdk.core").ObjectUtils;
var UiObjectBase = require("./uiobject_base").UiObjectBaseTestObject;
var UiObjectBaseDesc = require("./uiobject_base").UiObjectBaseDescription;

/**
 * The description of a RadioGroup test object in a mobile application.
 * @param {Object} [props] Initializes the description using the supplied property bag.
 * @returns {RadioGroup} the description object that was initialized.
 * @constructor
 * @memberof Mobile
 * @extends Mobile.UiObjectBaseDescription
 */
function RadioGroup(props) {
    var radioGroupDes = new UiObjectBaseDesc("RadioGroup");

    radioGroupDes._defineByPropertyBag(props);

    return radioGroupDes;
}

/**
 * A radio group object in a mobile application.
 * @class RadioGroupTO
 * @memberof Mobile
 * @constructor
 * @extends Mobile.UiObjectBaseTestObject
 * @mixes Mobile.RadioGroupTOBehavior._methods
 */
function RadioGroupTO(session, description) {
    description = description || RadioGroup();
    var radioGroupTO = new UiObjectBase("MobileRadioGroup", session, description);

    radioGroupTO._mergeBehavior(RadioGroupTOBehavior);

    return radioGroupTO;
}

//Consts
var selectWiredName = "Select";

/**
 * @memberof Mobile
 * @ignore
 */
var RadioGroupTOBehavior = {
    /**
     * @ignore
     * @memberof Mobile.RadioGroupTOBehavior
     */
    _methods: {
        /*jshint eqnull:true */
        /**
         * Selects the specified radio button in the group by index or by name.
         * @function
         * @param {...string | number} item  Name or index. <br/>
         * index: the 0-based index of the radio button to select.<br/>
         * name: the name of the radio button to select.
         * @returns {Promise<void>} a promise that is fulfilled after the requested selection occurred.
         *
         */
        select: {
            wiredName: selectWiredName,
            exec: function (item) {
                if (item == null)
                    throw new Error("You must pass the name or the index of the radio group button to select() method");

                return this._executeWithEvents(arguments, selectWiredName, item);
            }
        }
    }
};

module.exports.RadioGroup = RadioGroup;
module.exports.RadioGroupTO = RadioGroupTO;


