var ObjUtils = require("leanft.sdk.core").ObjectUtils;
var UiObjectBase = require("./uiobject_base").UiObjectBaseTestObject;
var UiObjectBaseDesc = require("./uiobject_base").UiObjectBaseDescription;

/**
 * The description of a Stepper test object in a mobile application.
 * @param {Object} [props] Initializes the description using the supplied property bag.
 * @returns {TabStrip} the description object that was initialized.
 * @constructor
 * @memberof Mobile
 * @extends Mobile.UiObjectBaseDescription
 */
function TabStrip(props) {
    var tabStripDes = new UiObjectBaseDesc("TabStrip");

    tabStripDes._defineByPropertyBag(props);

    return tabStripDes;
}

/**
 * A tab strip object in a mobile application. (iOS only)
 * @class TabStripTO
 * @memberof Mobile
 * @constructor
 * @extends Mobile.UiObjectBaseTestObject
 * @mixes Mobile.TabStripTOBehavior._methods
 */
function TabStripTO(session, description) {
    description = description || TabStrip();
    var tabStripTO = new UiObjectBase("MobileTabStrip", session, description);

    tabStripTO._mergeBehavior(TabStripTOBehavior);

    return tabStripTO;
}

//Consts
var selectWiredName = "Select";

/**
 * @memberof Mobile
 * @ignore
 */
var TabStripTOBehavior = {
    /**
     * @ignore
     * @memberof Mobile.TabStripTOBehavior
     */
    _methods: {
        /*jshint eqnull:true */
        /**
         * Selects the specified tab by index or by name (iOS only).
         * @function
         * @param {string | number} state The 0-based index of the tab to select or the name of the tab to select.
         * @returns {Promise<void>} a promise that is fulfilled after the requested tab is selected.
         */
        select: {
            wiredName: selectWiredName,
            exec: function (state) {
                if (state == null)
                    throw new Error("you must pass a number or string to select() method");

                return this._executeWithEvents(arguments, selectWiredName, state);
            }
        }
    }
};

module.exports.TabStrip = TabStrip;
module.exports.TabStripTO = TabStripTO;


