var UiObjectBase = require("./uiobject_base").UiObjectBaseTestObject;
var UiObjectBaseDesc = require("./uiobject_base").UiObjectBaseDescription;
var Behaviors = require("./behaviors.js");

/**
 * The description of a UiObject test object in a mobile application.
 * @param {Object} [props] Initializes the description using the supplied property bag.
 * @returns {UiObject} the description object that was initialized.
 * @constructor
 * @memberof Mobile
 * @extends Mobile.UiObjectBaseDescription
 */
function UiObject(props) {
    var uiobjectDes = new UiObjectBaseDesc("UiObject");

    uiobjectDes._defineByPropertyBag(props);

    return uiobjectDes;
}

/*
 * Constructor method that will be called by the factory.
 * @param {Session} session The session to which the constructed object belongs.
 * @param {Description} description The description of the test object as passed to the $ method of the parent.
 * @returns {UiObjectBase} the test object that contains all the functionality of the UiObjectTO test object.
 */
/**
 * A generic object in a mobile application.
 * @class UiObjectTO
 * @memberof Mobile
 * @constructor
 * @extends Mobile.UiObjectBaseTestObject
 * @mixes Mobile.SetFocusTOBehavior._methods
 */
function UiObjectTO(session, description) {
    description = description || UiObject();
    var uiobjectTO = new UiObjectBase("MobileObject", session, description);

    uiobjectTO._mergeBehavior(Behaviors.SetFocusTOBehavior);

    return uiobjectTO;
}

module.exports.UiObject = UiObject;
module.exports.UiObjectTO = UiObjectTO;