var ObjUtils = require("leanft.sdk.core").ObjectUtils;
var UiObjectBase = require("./uiobject_base").UiObjectBaseTestObject;
var UiObjectBaseDesc = require("./uiobject_base").UiObjectBaseDescription;
var Behaviors = require("./behaviors.js");

/**
 * The description of a WebView test object in a mobile application.
 * @param {Object} [props] Initializes the description using the supplied property bag.
 * @returns {WebView} the description object that was initialized.
 * @constructor
 * @memberof Mobile
 * @extends Mobile.UiObjectBaseDescription
 */
function WebView(props) {
    var webViewDes = new UiObjectBaseDesc("WebView");

    webViewDes._defineByPropertyBag(props);

    return webViewDes;
}


/**
 * A web view control in a mobile application.
 * @class WebViewTO
 * @memberof Mobile
 * @constructor
 * @extends Mobile.UiObjectBaseTestObject
 * @mixes Mobile.ScrollableTOBehavior._methods
 */
function WebViewTO(session, description) {
    description = description || WebView();
    var webViewTO = new UiObjectBase("MobileWebView", session, description);

    webViewTO._mergeBehavior(Behaviors.ScrollableTOBehavior);

    return webViewTO;
}

module.exports.WebView = WebView;
module.exports.WebViewTO = WebViewTO;



