var assert = require("assert");
var sinon = require("sinon");
var ButtonDesc = require("../lib/button.js").Button;

describe("Button Description",function() {
    var buttonDescUnderTest;
    beforeEach(function () {
        buttonDescUnderTest = new ButtonDesc();
    });

    describe("text method", function() {
        it("should add 'text' to _properties when value is given using property bag",function(){
            buttonDescUnderTest = ButtonDesc({
                text: "some value"
            });
            assert.strictEqual(buttonDescUnderTest._properties["text"], "some value");
        });
    });

});
