var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;

/**
 * The description of an Oracle Applications sign-on window.
 * @param {Object} [props] Initializes an LogonWindow test object using the supplied property bag.
 * @returns {Object} the description object that was initialized.
 * @class LogonWindow
 * @extends Oracle.UiObjectBaseDescription
 * @memberof Oracle
 */
function LogonWindow(props){
    var logonWindowDesc = new UiObjectBaseDescription("LogonWindow");

    logonWindowDesc._defineByPropertyBag(props);
    return logonWindowDesc;
}

/**
 * An Oracle Applications sign-on window.
 * @constructor
 * @class LogonWindowTO
 * @extends Oracle.UiObjectBaseTO
 * @memberof Oracle
 */
function LogonWindowTO(session,description){
    description = description || LogonWindow();
    var logonWindowTO = new UiObjectBaseTO("OracleLogon", session, description);
    return logonWindowTO;
}

module.exports.LogonWindowTO = LogonWindowTO;
module.exports.LogonWindow = LogonWindow;