var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;

/**
 * The description of an Oracle picklist control.
 * @param {Object} [props] Initializes an PickList test object using the supplied property bag.
 * @returns {Object} the description object that was initialized.
 * @class PickList
 * @extends Oracle.UiObjectBaseDescription
 * @memberof Oracle
 */
function PickList(props){
    var pickListDesc = new UiObjectBaseDescription("PickList");

    pickListDesc._defineByPropertyBag(props);
    return pickListDesc;
}

/**
 * An Oracle picklist control.
 * @constructor
 * @class PickListTO
 * @extends Oracle.UiObjectBaseTO
 * @memberof Oracle
 */
function PickListTO(session,description){
    description = description || PickList();
    var pickListTO = new UiObjectBaseTO("OraclePickList", session, description);
    return pickListTO;
}

module.exports.PickListTO = PickListTO;
module.exports.PickList = PickList;