var Core = require("leanft.sdk.core");
var Utils = Core.ObjectUtils;
var MergeBehavior = Core.MergeBehavior;

/**
 * A Radio Group Item object
 * @class RadioGroupItem
 * @mixes Oracle.RadioGroupItemBehavior._properties
 * @memberof Oracle
 */
function RadioGroupItem(ownerRadioGroup, identifier) {
    Utils.extend(this, MergeBehavior);
    this._mergeBehavior(RadioGroupItemBehavior);
    this._ownerRadioGroup = ownerRadioGroup;
    this._identifier = identifier;
    this._executeWithEventsOnItemOnError = this._ownerRadioGroup._executeWithEventsOnItemOnError.bind(this._ownerRadioGroup, this);
}

RadioGroupItem.prototype = {
    _ownerRadioGroup: null,
    _identifier: null,
    _executeWithEventsOnItemOnError: null
};

/**
 * The methods of RadioGroupItem object
 * @memberof Oracle
 * @ignore
 */
var RadioGroupItemBehavior = {
    _helpers: {
        /**
         * Returns the identifier of the item.
         * @returns {number|string} the identifier of the item.
         * @ignore
         */
        _getIdentifier: function () {
            return this._identifier;
        },
        /**
         * Returns the text representation of the item.
         * @returns {string} the text representation of the item.
         * @ignore
         */
        _getTextRepresentation: function () {
            return this._identifier;
        }
    },

    /**
     * @memberof Oracle.RadioGroupItemBehavior
     * @ignore
     */
    _properties: {
        /**
         * Indicates whether the specified radio button is selected in the option group.
         * @function
         * @returns {Promise<boolean>} a promise fulfilled with the indication whether the item is selected or not.
         */
        isSelected: function () {
            return this._executeWithEventsOnItemOnError(this._ownerRadioGroup._isItemSelected, this._identifier);
        }
    }
};

module.exports.RadioGroupItem = RadioGroupItem;
