var assert = require("assert");

var OracleFactory = require("../lib/oracle_factory.js").OracleFactory;

describe("OracleFactory", function () {
    var oracleFactoryUnderTest;
    var sessionMock;

    beforeEach(function () {
        sessionMock = {

        };
        oracleFactoryUnderTest = new OracleFactory({});
    });

    describe("all test objects tests", function () {
        it("should have waitUntilEnabled function if they have isEnabled function", function () {
            Object.keys(oracleFactoryUnderTest._constructors).forEach(function(type) {
                var constructor =  oracleFactoryUnderTest._constructors[type];
                var testObject = new constructor(sessionMock, null);

                if(testObject.isEnabled) {
                    assert(testObject.waitUntilEnabled, "testObject: " + type + " does not have waitUntilEnabled");
                }
            });
        });
    });
});