var assert = require("assert");
var sinon = require("sinon");

describe("TableCell", function () {
    var TableCell = require("../lib/table_cell.js").TableCell;
    var tableCellUnderTest;
    var owner;
    beforeEach(function () {
        owner = {owner: "Owner", _executeWithEventsOnItem: sinon.stub(), _executeWithEventsOnItemOnError: sinon.stub()};
        tableCellUnderTest = new TableCell(owner, 0, 0);
        tableCellUnderTest._executeWithEventsOnItem = sinon.stub();
        tableCellUnderTest._executeWithEventsOnItemOnError = sinon.stub();
    });

    describe("Constructor", function () {
        it("should assign correct values", function () {
            var parent = owner;
            tableCellUnderTest = new TableCell(parent, 0, 0);
            assert.strictEqual(tableCellUnderTest._ownerTable, parent);
            assert.strictEqual(tableCellUnderTest._rowIdentifier, 0);
            assert.strictEqual(tableCellUnderTest._columnIdentifier, 0);
        });
    });

    describe("value method", function () {
        it("should execute _getCellValue with correct parameter", function () {
            tableCellUnderTest._ownerTable._getCellValue = sinon.stub();
            tableCellUnderTest.value();
            sinon.assert.calledOnce(tableCellUnderTest._executeWithEventsOnItemOnError);
            sinon.assert.calledWith(tableCellUnderTest._executeWithEventsOnItemOnError, tableCellUnderTest._ownerTable._getCellValue, 0, 0);
        });

        it("should return what's returned from _getCellValue as is", function () {
            tableCellUnderTest._executeWithEventsOnItemOnError.returns("some text");
            var res = tableCellUnderTest.value();
            assert.strictEqual(res, "some text");
        });
    });

    describe("isEditable method", function () {
        it("should execute _isEditable with correct parameter", function () {
            tableCellUnderTest._ownerTable._isEditable = sinon.stub();
            tableCellUnderTest.isEditable();
            sinon.assert.calledOnce(tableCellUnderTest._executeWithEventsOnItemOnError);
            sinon.assert.calledWith(tableCellUnderTest._executeWithEventsOnItemOnError, tableCellUnderTest._ownerTable._isEditable, 0, 0);
        });

        it("should return what's returned from _isEditable as is", function () {
            tableCellUnderTest._executeWithEventsOnItemOnError.returns("some text");
            var res = tableCellUnderTest.isEditable();
            assert.strictEqual(res, "some text");
        });
    });

    describe("setValue method", function () {
        it("should execute _setCellValue with correct parameter", function () {
            tableCellUnderTest._ownerTable._setCellValue = sinon.stub();
            tableCellUnderTest.setValue("some text");
            sinon.assert.calledOnce(tableCellUnderTest._executeWithEventsOnItem);
            sinon.assert.calledWith(tableCellUnderTest._executeWithEventsOnItem, tableCellUnderTest._ownerTable._setCellValue, 0, 0, "some text");
        });

        it("should return the promise as is", function () {
            var promiseMock = {};
            tableCellUnderTest._executeWithEventsOnItem.returns(promiseMock);
            var res = tableCellUnderTest.setValue("some text");
            assert.strictEqual(res, promiseMock);
        });
    });

    describe("setValueAndValidate method", function () {
        it("should execute _setCellValueAndValidate with correct parameter", function () {
            tableCellUnderTest._ownerTable._setCellValueAndValidate = sinon.stub();
            tableCellUnderTest.setValueAndValidate("some text");
            sinon.assert.calledOnce(tableCellUnderTest._executeWithEventsOnItem);
            sinon.assert.calledWith(tableCellUnderTest._executeWithEventsOnItem, tableCellUnderTest._ownerTable._setCellValueAndValidate, 0, 0, "some text");
        });

        it("should return the promise as is", function () {
            var promiseMock = {};
            tableCellUnderTest._executeWithEventsOnItem.returns(promiseMock);
            var res = tableCellUnderTest.setValueAndValidate("some text");
            assert.strictEqual(res, promiseMock);
        });
    });

    describe("asTestObject method", function () {
        it("should execute _getCellItem with correct parameter", function () {
            tableCellUnderTest._ownerTable._getAsTestObject = sinon.stub();
            tableCellUnderTest.asTestObject();
            sinon.assert.calledOnce(tableCellUnderTest._executeWithEventsOnItem);
            sinon.assert.calledWith(tableCellUnderTest._executeWithEventsOnItem, tableCellUnderTest._ownerTable._getAsTestObject, 0, 0);
        });

        it("should return the promise as is", function () {
            var promiseMock = {};
            tableCellUnderTest._executeWithEventsOnItem.returns(promiseMock);
            var res = tableCellUnderTest.asTestObject();
            assert.strictEqual(res, promiseMock);
        });
    });

    describe("openDialog method", function () {
        it("should execute _openDialog with correct parameter", function () {
            tableCellUnderTest._ownerTable._openDialog = sinon.stub();
            tableCellUnderTest.openDialog("some text");
            sinon.assert.calledOnce(tableCellUnderTest._executeWithEventsOnItem);
            sinon.assert.calledWith(tableCellUnderTest._executeWithEventsOnItem, tableCellUnderTest._ownerTable._openDialog, 0, 0);
        });

        it("should return the promise as is", function () {
            var promiseMock = {};
            tableCellUnderTest._executeWithEventsOnItem.returns(promiseMock);
            var res = tableCellUnderTest.openDialog();
            assert.strictEqual(res, promiseMock);
        });
    });

    describe("setFocus method", function () {
        it("should execute _setFocus with correct parameter", function () {
            tableCellUnderTest._ownerTable._setFocus = sinon.stub();
            tableCellUnderTest.setFocus("some text");
            sinon.assert.calledOnce(tableCellUnderTest._executeWithEventsOnItem);
            sinon.assert.calledWith(tableCellUnderTest._executeWithEventsOnItem, tableCellUnderTest._ownerTable._setFocus, 0, 0);
        });

        it("should return the promise as is", function () {
            var promiseMock = {};
            tableCellUnderTest._executeWithEventsOnItem.returns(promiseMock);
            var res = tableCellUnderTest.setFocus("some text");
            assert.strictEqual(res, promiseMock);
        });
    });
});