var Core = require("leanft.sdk.core");
var Utils = Core.ObjectUtils;
var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;
var StdWinBehaviors = require("leanft.sdk.stdwin").Behaviors;
var StdWinUiObjectWithLayoutInfoBehaviour = StdWinBehaviors.UiObjectWithLayoutInfoBehaviour;
var StdWinUiObjectWithLayoutInfoDescription = StdWinBehaviors.UiObjectWithLayoutInfoDescription;
var StdWinCheckedStateDescriptionBehavior = StdWinBehaviors.CheckedStateDescriptionBehavior;
var StdWinCheckedStateTOBehavior = StdWinBehaviors.CheckedStateTOBehavior;
var StdWinCheckBoxDescriptionBehavior = StdWinBehaviors.CheckBoxDescriptionBehavior;
var StdWinCheckBoxTOBehavior = StdWinBehaviors.CheckBoxTOBehavior;
/**
 * The description of a CheckBox test object.
 * @param {Object} [props] Initializes a CheckBox test object using the supplied property bag.
 * @returns {CheckBox} the description object that was initialized.
 * @class CheckBox
 * @memberof PowerBuilder
 * @extends PowerBuilder.UiObjectBaseDescription
 * @mixes StdWin.UiObjectWithLayoutInfoDescription
 * @mixes StdWin.CheckBoxDescriptionBehavior
 */
function CheckBox(props) {
    var checkBoxDesc = new UiObjectBaseDescription("CheckBox");
    Utils.extend(checkBoxDesc, StdWinUiObjectWithLayoutInfoDescription);
    Utils.extend(checkBoxDesc, StdWinCheckedStateDescriptionBehavior);
    Utils.extend(checkBoxDesc, StdWinCheckBoxDescriptionBehavior);

    checkBoxDesc._defineByPropertyBag(props);
    return checkBoxDesc;
}

/**
 * A CheckBox object
 * @constructor
 * @class CheckBoxTO
 * @extends PowerBuilder.UiObjectBaseTO
 * @mixes PowerBuilder.UiObjectWithLayoutInfoBehaviour._properties
 * @mixes StdWin.CheckBoxTOBehavior._methods
 * @mixes StdWin.CheckBoxTOBehavior._properties
 * @memberof PowerBuilder
 */
function CheckBoxTO(session, description){
    description = description || CheckBox();
    var checkBoxTO = new UiObjectBaseTO("PbCheckBox", session, description);
    checkBoxTO._mergeBehavior(StdWinUiObjectWithLayoutInfoBehaviour);
    checkBoxTO._mergeBehavior(StdWinCheckedStateTOBehavior);
    checkBoxTO._mergeBehavior(StdWinCheckBoxTOBehavior);

    return checkBoxTO;
}


module.exports.CheckBox = CheckBox;
module.exports.CheckBoxTO = CheckBoxTO;
