var Core = require("leanft.sdk.core");
var Utils = Core.ObjectUtils;
var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;
var StdWinBehaviors = require("leanft.sdk.stdwin").Behaviors;
var StdWinUiObjectWithLayoutInfoBehaviour = StdWinBehaviors.UiObjectWithLayoutInfoBehaviour;
var StdWinUiObjectWithLayoutInfoDescription = StdWinBehaviors.UiObjectWithLayoutInfoDescription;
var StdWinComboBoxDescriptionBehavior =  StdWinBehaviors.ComboBoxDescriptionBehavior;
var StdWinComboBoxTOBehavior =  StdWinBehaviors.ComboBoxTOBehavior;

/**
 * The description of a ComboBox test object.
 * @param {Object} [props] Initializes a ComboBox test object using the supplied property bag.
 * @returns {ComboBox} the description object that was initialized.
 * @class ComboBox
 * @memberof PowerBuilder
 * @extends PowerBuilder.UiObjectBaseDescription
 * @mixes StdWin.UiObjectWithLayoutInfoDescription
 * @mixes StdWin.ComboBoxDescriptionBehavior
 */
function ComboBox(props) {
    var comboBoxDesc = new UiObjectBaseDescription("ComboBox");
    Utils.extend(comboBoxDesc, StdWinUiObjectWithLayoutInfoDescription);
    Utils.extend(comboBoxDesc, StdWinComboBoxDescriptionBehavior);
    comboBoxDesc._defineByPropertyBag(props);
    return comboBoxDesc;
}

/**
 * A ComboBox object
 * @constructor
 * @class ComboBoxTO
 * @extends PowerBuilder.UiObjectBaseTO
 * @mixes PowerBuilder.UiObjectWithLayoutInfoBehaviour._properties
 * @mixes StdWin.ComboBoxTOBehavior._properties
 * @mixes StdWin.ComboBoxTOBehavior._methods
 * @memberof PowerBuilder
 */
function ComboBoxTO(session, description) {
    description = description || ComboBox();
    var comboBoxTO = new UiObjectBaseTO("PbComboBox", session, description);
    comboBoxTO._mergeBehavior(StdWinUiObjectWithLayoutInfoBehaviour);
    comboBoxTO._mergeBehavior(StdWinComboBoxTOBehavior);
    return comboBoxTO;
}

module.exports.ComboBox = ComboBox;
module.exports.ComboBoxTO = ComboBoxTO;
