var Core = require("leanft.sdk.core");
var Utils = Core.ObjectUtils;
var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;
var StdWinBehaviors = require("leanft.sdk.stdwin").Behaviors;
var StdWinUiObjectWithLayoutInfoBehaviour = StdWinBehaviors.UiObjectWithLayoutInfoBehaviour;
var StdWinUiObjectWithLayoutInfoDescription = StdWinBehaviors.UiObjectWithLayoutInfoDescription;
var StdWinEditTOBehavior = StdWinBehaviors.EditTOBehavior;

/**
 * The description of an EditField test object.
 * @param {Object} [props] Initializes a EditField test object using the supplied property bag.
 * @returns {Edit} the description object that was initialized.
 * @class Edit
 * @memberof PowerBuilder
 * @extends PowerBuilder.UiObjectBaseDescription
 * @mixes StdWin.UiObjectWithLayoutInfoDescription
 */
function Edit(props) {
    var editDesc = new UiObjectBaseDescription("EditField");
    Utils.extend(editDesc, StdWinUiObjectWithLayoutInfoDescription);
    editDesc._defineByPropertyBag(props);
    return editDesc;
}

/**
 * An EditField object
 * @constructor
 * @class EditTO
 * @extends PowerBuilder.UiObjectBaseTO
 * @mixes PowerBuilder.UiObjectWithLayoutInfoBehaviour._properties
 * @mixes StdWin.EditTOBehavior._methods
 * @memberof PowerBuilder
 */
function EditTO(session, description) {
    description = description || Edit();
    var editTO = new UiObjectBaseTO("PbEdit", session, description);
    editTO._mergeBehavior(StdWinUiObjectWithLayoutInfoBehaviour);
    editTO._mergeBehavior(StdWinEditTOBehavior);
    return editTO;
}

module.exports.Edit = Edit;
module.exports.EditTO = EditTO;
