var Core = require("leanft.sdk.core");
var Utils = Core.ObjectUtils;
var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;
var StdWinBehaviors = require("leanft.sdk.stdwin").Behaviors;
var StdWinUiObjectWithLayoutInfoBehaviour = StdWinBehaviors.UiObjectWithLayoutInfoBehaviour;
var StdWinUiObjectWithLayoutInfoDescription = StdWinBehaviors.UiObjectWithLayoutInfoDescription;
var StdWinListBoxTOBehavior = StdWinBehaviors.ListBoxTOBehavior;
var ListBaseBehavior = Core.ListBaseBehavior;
var ListBoxDescriptionBaseBehavior = Core.ListBoxDescriptionBaseBehavior;
var ContainerBehavior = Core.ContainerBehavior;
var WrappedItemExecutorBehavior = Core.ExecuteItemsWithEventsBehavior;

/**
 * The description of a ListBox test object.
 * @param {Object} [props] Initializes a ListBox test object using the supplied property bag.
 * @returns {ListBox} the description object that was initialized.
 * @class ListBox
 * @memberof PowerBuilder
 * @extends PowerBuilder.UiObjectBaseDescription
 * @mixes StdWin.UiObjectWithLayoutInfoDescription
 * @mixes ListBoxDescriptionBaseBehavior
 */
function ListBox(props) {
    var listBoxDesc = new UiObjectBaseDescription("ListBox");
    Utils.extend(listBoxDesc, StdWinUiObjectWithLayoutInfoDescription);
    Utils.extend(listBoxDesc, ListBoxDescriptionBaseBehavior);
    listBoxDesc._defineByPropertyBag(props);
    return listBoxDesc;
}

/**
 * A ListBox object
 * @constructor
 * @class ListBoxTO
 * @extends PowerBuilder.UiObjectBaseTO
 * @mixes PowerBuilder.UiObjectWithLayoutInfoBehaviour._properties
 * @mixes Core.ContainerBehavior
 * @mixes ListBaseBehavior._methods
 * @mixes Core.WrappedItemExecutorBehavior
 * @mixes ListBoxTOBehavior._methods
 * @memberof PowerBuilder
 */
function ListBoxTO(session, description) {
    description = description || ListBox();
    var listBoxTO = new UiObjectBaseTO("PbList", session, description);
    listBoxTO._mergeBehavior(StdWinUiObjectWithLayoutInfoBehaviour);
    listBoxTO._mergeBehavior(ContainerBehavior);
    listBoxTO._mergeBehavior(ListBaseBehavior);
    listBoxTO._mergeBehavior(WrappedItemExecutorBehavior);
    listBoxTO._mergeBehavior(StdWinListBoxTOBehavior);

    return listBoxTO;
}

module.exports.ListBox = ListBox;
module.exports.ListBoxTO = ListBoxTO;
