var Core = require("leanft.sdk.core");
var Utils = Core.ObjectUtils;
var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;
var StdWinBehaviors = require("leanft.sdk.stdwin").Behaviors;
var StdWinUiObjectWithLayoutInfoBehaviour = StdWinBehaviors.UiObjectWithLayoutInfoBehaviour;
var StdWinUiObjectWithLayoutInfoDescription = StdWinBehaviors.UiObjectWithLayoutInfoDescription;
var StdWinCheckedStateDescriptionBehavior = StdWinBehaviors.CheckedStateDescriptionBehavior;
var StdWinCheckedStateTOBehavior = StdWinBehaviors.CheckedStateTOBehavior;
var StdWinRadioButtonDescriptionBehavior = StdWinBehaviors.RadioButtonDescriptionBehavior;
var StdWinRadioButtonTOBehavior = StdWinBehaviors.RadioButtonTOBehavior;

/**
 * The description of a RadioButton test object.
 * @param {Object} [props] Initializes a RadioButton test object using the supplied property bag.
 * @returns {RadioButton} the description object that was initialized.
 * @class RadioButton
 * @memberof PowerBuilder
 * @extends PowerBuilder.UiObjectBaseDescription
 * @mixes StdWin.UiObjectWithLayoutInfoDescription
 * @mixes StdWin.RadioButtonDescriptionBehavior
 */
function RadioButton(props) {
    var radioButtonDesc = new UiObjectBaseDescription("RadioButton");
    Utils.extend(radioButtonDesc, StdWinUiObjectWithLayoutInfoDescription);
    Utils.extend(radioButtonDesc, StdWinCheckedStateDescriptionBehavior);
    Utils.extend(radioButtonDesc, StdWinRadioButtonDescriptionBehavior);
    radioButtonDesc._defineByPropertyBag(props);
    return radioButtonDesc;
}

/**
 * A RadioButton object
 * @constructor
 * @class RadioButtonTO
 * @extends PowerBuilder.UiObjectBaseTO
 * @mixes PowerBuilder.UiObjectWithLayoutInfoBehaviour._properties
 * @mixes StdWin.RadioButtonTOBehavior._properties
 * @mixes StdWin.RadioButtonTOBehavior._methods
 * @memberof PowerBuilder
 */
function RadioButtonTO(session, description) {
    description = description || RadioButton();
    var radioButtonTO = new UiObjectBaseTO("PbRadioButton", session, description);
    radioButtonTO._mergeBehavior(StdWinUiObjectWithLayoutInfoBehaviour);
    radioButtonTO._mergeBehavior(StdWinCheckedStateTOBehavior);
    radioButtonTO._mergeBehavior(StdWinRadioButtonTOBehavior);
    return radioButtonTO;
}

module.exports.RadioButton = RadioButton;
module.exports.RadioButtonTO = RadioButtonTO;
