var Core = require("leanft.sdk.core");
var Utils = Core.ObjectUtils;
var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;
var StdWinBehaviors = require("leanft.sdk.stdwin").Behaviors;
var StdWinUiObjectWithLayoutInfoBehaviour = StdWinBehaviors.UiObjectWithLayoutInfoBehaviour;
var StdWinUiObjectWithLayoutInfoDescription = StdWinBehaviors.UiObjectWithLayoutInfoDescription;

/**
 * The description of a PowerBuilder UiObject test object.
 * @param {Object} [props] Initializes a PowerBuilder test object using the supplied property bag.
 * @returns {UiObject} the description object that was initialized.
 * @class UiObject
 * @memberof PowerBuilder
 * @extends PowerBuilder.UiObjectBaseDescription
 * @mixes StdWin.UiObjectWithLayoutInfoDescription
 */
function UiObject(props) {
    var uiObjectDesc = new UiObjectBaseDescription("UiObject");
    Utils.extend(uiObjectDesc, StdWinUiObjectWithLayoutInfoDescription);

    uiObjectDesc._defineByPropertyBag(props);
    return uiObjectDesc;
}

/**
 * A PowerBuilder object
 * @constructor
 * @class UIObjectTO
 * @extends PowerBuilder.UiObjectBaseTO
 * @mixes StdWin.UiObjectWithLayoutInfoBehaviour._properties
 * @memberof PowerBuilder
 */
function UIObjectTO(session, description) {
    description = description || UiObject();
    var uiObjectTO = new UiObjectBaseTO("PbObject", session, description);
    uiObjectTO._mergeBehavior(StdWinUiObjectWithLayoutInfoBehaviour);
    return uiObjectTO;
}


module.exports.UiObject = UiObject;
module.exports.UIObjectTO = UIObjectTO;
