var SapGuiObjectBase = require("./sapgui_object_base.js");
var SapGuiBaseDescription = SapGuiObjectBase.SapGuiBaseDescription;
var SapGuiBaseTO = SapGuiObjectBase.SapGuiBaseTO;


/**
 * The description of a SAP GUI for Windows element
 * @param {Object} [props] Initializes a SAP GUI for windows element test object using the supplied property bag.
 * @returns {Element} the description object that was initialized.
 * @class Element
 * @memberof SAPGUI
 * @extends SAPGUI.SapGuiBaseDescription
 */
function Element(props){
    var elementDesc = new SapGuiBaseDescription("Element");
    elementDesc._defineByPropertyBag(props);
    return elementDesc;
}

/**
 * An ElementTO object
 * @class ElementTO
 * @memberof SAPGUI
 * @constructor
 * @extends SAPGUI.SapGuiBaseTO
 */
function ElementTO(session,description) {
    description = description || Element();
    var elementTO = new SapGuiBaseTO("SAPGuiElement", session, description);
    return elementTO;
}

module.exports.Element = Element;
module.exports.ElementTO = ElementTO;
