var Core = require("leanft.sdk.core");
var ObjUtils = Core.ObjectUtils;
var Convert = Core.Convert;

var SapGuiObjectBase = require("./sapgui_object_base.js");
var SapGuiBaseDescription = SapGuiObjectBase.SapGuiBaseDescription;
var SapGuiBaseTO = SapGuiObjectBase.SapGuiBaseTO;


/**
 * Description of a SAP GUI for Windows OK Code box.
 * @memberof SAPGUI
 * @ignore
 */
var OKCodeDescriptionBehaviour = {
    /**
     * The command code entered in the OK box.
     * @param {string} [value] When provided, this method acts as a setter and sets the value.
     * @returns {string | Description} If no parameter is provided, the method acts as a getter and returns the current stored value;
     * otherwise it returns 'this' for use with the builder pattern.
     */
    value: function (value) {
        return this._simplePropertyHandler("value", value);
    }
};

/**
 * @param {Object} [props] Initializes a OKCode test object using the supplied property bag.
 * @returns {OKCode} the description object that was initialized.
 * @constructor
 * @memberof SAPGUI
 * @extends SAPGUI.SapGuiBaseDescription
 * @mixes SAPGUI.OKCodeDescriptionBehaviour
 */
function OKCode(props){
    var okCodeDesc = new SapGuiBaseDescription("OKCode");
    ObjUtils.extend(okCodeDesc, OKCodeDescriptionBehaviour);
    okCodeDesc._defineByPropertyBag(props);
    return okCodeDesc;
}

/**
 * A OKCodeTO object,
 * @constructor
 * @class OKCodeTO
 * @extends SAPGUI.ElementTO
 * @mixes SAPGUI.OKCodeTOBehavior._properties
 * @mixes SAPGUI.OKCodeTOBehavior._methods
 * @memberof SAPGUI
 */
function OKCodeTO(session, description) {
    description = description || OKCode();
    var okCodeTO = new SapGuiBaseTO( "SAPGuiOKCode", session, description);
    okCodeTO._mergeBehavior(OKCodeTOBehavior);
    return okCodeTO;
}


/**
 * The behaviors of the OKCodeTO object
 * @memberof SAPGUI
 * @ignore
 */
var OKCodeTOBehavior = {
    /**
     * @memberof SAPGUI.OKCodeTOBehavior
     * @ignore
     */
    _properties: {
        /**
         * The command code entered in the field.
         * @function
         * @returns {Promise<string>} a promise that is fulfilled with the command code entered in the field.
         */
        value: function () {
            return this._getROProperty("value", arguments);
        }
    },

    /**
     * @memberof SAPGUI.OKCodeTOBehavior
     * @ignore
     */
    _methods: {
        /*jshint eqnull:true */
        /**
         * Sets the OKCode command.
         * @function
         * @param {string} value The text value to enter in the OKCode.
         * To execute the OKCode command, send an ENTER key with SendKey method of the parent IWindow.
         * @returns {Promise<void>} a promise that is fulfilled when the operation ends.
         */
        setValue: function (value) {
            if (value == null)
                throw new Error("The value cannot be null.");
            return this._executeWithEvents(arguments, "Set", value);
        }
    }
};

module.exports.OKCode = OKCode;
module.exports.OKCodeTO = OKCodeTO;
