var Core = require("leanft.sdk.core");
var Logger = Core.Logger;
var ObjectUtils = Core.ObjectUtils;
var ConcreteFactoryBehavior = Core.ConcreteFactoryBehavior;

var GuiSessionTO = require("./gui_session.js").GuiSessionTO;
var ElementTO = require("./element.js").ElementTO;
var WindowTO = require("./window.js").WindowTO;
var TableTO = require("./table.js").TableTO;
var LabelTO = require("./label.js").LabelTO;
var OKCodeTO = require("./ok_code.js").OKCodeTO;
var EditTO = require("./edit.js").EditTO;
var EditorTO = require("./editor.js").EditorTO;
var GridTO = require("./grid.js").GridTO;
var APOGridTO = require("./apogrid.js").APOGridTO;
var ComboBoxTO = require("./combo_box.js").ComboBoxTO;
var ButtonTO = require("./button.js").ButtonTO;
var CheckBoxTO = require("./check_box.js").CheckBoxTO;
var RadioButtonTO = require("./radio_button.js").RadioButtonTO;
var TreeViewTO = require("./tree_view.js").TreeViewTO;
var StatusBarTO = require("./status_bar.js").StatusBarTO;
var TabControlTO = require("./tab_control.js").TabControlTO;
var CalendarTO = require("./calendar.js").CalendarTO;
var MenubarTO = require("./menu_bar.js").MenubarTO;
var ToolBarTO = require("./tool_bar.js").ToolBarTO;

function SapGuiFactory(session){
    this._logger = new Logger("SAPGUI.TestObjectFactory");
    this._session = session;
    this._technology = "SAPGUI";

    ObjectUtils.extend(this, ConcreteFactoryBehavior);
}

SapGuiFactory.prototype = {
    _logger: null,
    _session: null,
    _technology: null,

    _constructors: {
        "guisession": GuiSessionTO,
        "element": ElementTO,
        "window": WindowTO,
        "table": TableTO,
        "label": LabelTO,
        "okcode": OKCodeTO,
        "editfield": EditTO,
        "editor": EditorTO,
        "grid": GridTO,
        "combobox": ComboBoxTO,
        "apogrid": APOGridTO,
        "button": ButtonTO,
        "checkbox": CheckBoxTO,
        "radiobutton": RadioButtonTO,
        "treeview": TreeViewTO,
        "statusbar": StatusBarTO,
        "tabcontrol": TabControlTO,
        "calendar": CalendarTO,
        "menubar": MenubarTO,
        "toolbar": ToolBarTO
    },

    _nativeClassToLeanFtType: {
        "sapguisession": "guisession",
        "sapguielement": "element",
        "sapguiwindow": "window",
        "sapguitable": "table",
        "sapguilabel": "label",
        "sapguiokcode": "okcode",
        "sapguiedit": "editfield",
        "sapguitextarea": "editor",
        "sapguigrid": "grid",
        "sapguicombobox": "combobox",
        "sapguiapogrid": "apogrid",
        "sapguibutton": "button",
        "sapguicheckbox": "checkbox",
        "sapguiradiobutton": "radiobutton",
        "sapguitree": "treeview",
        "sapguistatusbar": "statusbar",
        "sapguitabstrip": "tabcontrol",
        "sapguicalendar": "calendar",
        "sapguimenubar": "menubar",
        "sapguitoolbar": "toolbar"
    }
};
module.exports.SapGuiFactory = SapGuiFactory;
