var Core = require("leanft.sdk.core");
var Convert = Core.Convert;
var Utils = Core.ObjectUtils;
var MergeBehavior = Core.MergeBehavior;
var TableCellBase = require("./table_cell_base.js");
var CellBaseOperationsBehavior = TableCellBase.CellBaseOperationsBehavior;

/**
 * A TableCell object
 * @constructor
 * @class TableCell
 * @mixes SAPGUI.CellBaseOperationsBehavior._properties
 * @mixes SAPGUI.CellBaseOperationsBehavior._methods
 * @mixes SAPGUI.TableCellBehavior._properties
 * @memberof SAPGUI
 */
function TableCell(owner, rowIndex, columnIdentifier) {
    Utils.extend(this, MergeBehavior);
    this._mergeBehavior(CellBaseOperationsBehavior);
    this._mergeBehavior(TableCellBehavior);
    this._ownerTable = owner;
    this._rowNumber = this._ownerTable._rowTransform(rowIndex);
    this._columnIdentifier = this._ownerTable._columnTransform(columnIdentifier);
    this._executeWithEventsOnItem = this._ownerTable._executeWithEventsOnItem.bind(this._ownerTable, this);
}

/**
 * The methods of TableCell object
 * @memberof SAPGUI
 * @ignore
 */
TableCell.prototype = {
    _rowNumber: null,
    _columnIdentifier: null,
    _ownerTable: null,
    _executeWithEventsOnItem: null
};

var TableCellBehavior = {
    /**
     * @memberof SAPGUI.TableCellBehavior
     * @ignore
     */
    _properties: {
        /**
         * The number of characters that can be entered in the cell. -1 if the cell does not have a valid length.
         * @function
         * @returns {Promise<number>} a promise that is fulfilled with the number of characters that can be entered in the cell.
         */
        length: function () {
            return this._ownerTable._getCellLength(this._rowNumber, this._columnIdentifier);
        }
    }
};

module.exports.TableCell = TableCell;
