var sinon = require("sinon");
var assert = require("assert");
var Core = require("leanft.sdk.core");
var ComponentType = require("../lib/component_type.js").ComponentType;
var TestObjectBase = Core.TestObjectBase;


describe("Element Description", function () {

    var Element = require("../lib/element.js").Element;
    var elementDescriptionUnderTest;

    beforeEach(function () {
        elementDescriptionUnderTest = new Element();
    });

    describe("Constructor", function () {
        it("should assign 'SAPGUI' and 'Element' LeanFT type if leanFT type is undefined", function () {
            elementDescriptionUnderTest = new Element();
            assert.deepEqual(elementDescriptionUnderTest.leanFTType, {
                technology: "SAPGUI",
                leanftType: "Element"
            });
        });

    });
});