var Core = require("leanft.sdk.core");
var SAPUI5BaseDesc = require("./sapui5_base.js").SAPUI5BaseDesc;
var SAPUI5BaseTestObject = require("./sapui5_base.js").SAPUI5BaseTestObject;
var HelperBehaviors = require("./helper_behaviors.js");

/**
 * The description of an SAPUI5 Carousel test object.
 * @param {Object} [props] Initializes a Carousel test object using the supplied property bag.
 * @returns {Carousel} the description object that was initialized.
 * @constructor
 * @memberof SAPUI5
 * @extends SAPUI5.SAPUI5BaseDesc
 * @mixes SAPUI5.EnabledDescriptionBehavior
 * @mixes SAPUI5.OrientationDescriptionBehavior
 * @mixes SAPUI5.ItemsCountDescriptionBehavior
 */
function Carousel(props) {
    var carouselDes = new SAPUI5BaseDesc("Carousel");

    Core.ObjectUtils.extend(carouselDes, HelperBehaviors.EnabledDescriptionBehavior);
    Core.ObjectUtils.extend(carouselDes, HelperBehaviors.OrientationDescriptionBehavior);
    Core.ObjectUtils.extend(carouselDes, HelperBehaviors.ItemsCountDescriptionBehavior);

    carouselDes._defineByPropertyBag(props);

    return carouselDes;
}

/**
 * An SAPUI5 Carousel.
 * @constructor
 * @memberof SAPUI5
 * @class CarouselTO
 * @extends SAPUI5.SAPUI5BaseTestObject
 * @mixes SAPUI5.EnabledTOBehavior._properties
 * @mixes SAPUI5.OrientationTOBehavior._properties
 * @mixes SAPUI5.NavigationTOBehavior._methods
 * @mixes SAPUI5.CarouselTOBehavior._properties
 * @mixes WaitUntilEnabledBehavior._methods
 */
function CarouselTO(session, description) {
    description = description || Carousel();
    var carouselTO = new SAPUI5BaseTestObject("SAPUICarousel", session, description);

    carouselTO._mergeBehavior(HelperBehaviors.EnabledTOBehavior);
    carouselTO._mergeBehavior(HelperBehaviors.OrientationTOBehavior);
    carouselTO._mergeBehavior(HelperBehaviors.NavigationTOBehavior);
    carouselTO._mergeBehavior(CarouselTOBehavior);

    return carouselTO;
}

/**
 * @ignore
 * @memberof SAPUI5
 */
var CarouselTOBehavior = {
    /**
     * @ignore
     * @memberof SAPUI5.CarouselTOBehavior
     */
    _properties: {
        /**
         * The number of items contained in the carousel.
         * @function
         * @returns {Promise<number>} a promise that is fulfilled with the number of items contained in the carousel.
         */
        itemsCount: {
            type: 'uint',
            wiredName: "items count"
        }
    }
};

module.exports.Carousel = Carousel;
module.exports.CarouselTO = CarouselTO;

