var Core = require("leanft.sdk.core");
var Web = require("leanft.sdk.web").Behaviors;
var SAPUI5BaseDesc = require("./sapui5_base.js").SAPUI5BaseDesc;
var SAPUI5BaseTestObject = require("./sapui5_base.js").SAPUI5BaseTestObject;
var HelperBehaviors = require("./helper_behaviors.js");

/**
 * The description of an SAPUI5 CheckBox test object.
 * @param {Object} [props] Initializes a CheckBox test object using the supplied property bag.
 * @returns {CheckBox} the description object that was initialized.
 * @constructor
 * @memberof SAPUI5
 * @extends SAPUI5.SAPUI5BaseDesc
 * @mixes Web.FormFieldBehavior
 * @mixes SAPUI5.CheckBoxDescriptionBehavior
 */
function CheckBox(props) {
    var checkBoxDes = new SAPUI5BaseDesc("CheckBox");

    Core.ObjectUtils.extend(checkBoxDes, Web.FormFieldBehavior);
    Core.ObjectUtils.extend(checkBoxDes, HelperBehaviors.CheckedDescriptionBehavior);
    Core.ObjectUtils.extend(checkBoxDes, HelperBehaviors.EnabledDescriptionBehavior);

    checkBoxDes._defineByPropertyBag(props);

    return checkBoxDes;
}

/**
 * An SAPUI5 CheckBox.
 * @constructor
 * @memberof SAPUI5
 * @class CheckBoxTO
 * @extends SAPUI5.SAPUI5BaseTestObject
 * @mixes Web.CheckBoxTOBehavior._properties
 * @mixes Web.CheckBoxTOBehavior._methods
 * @mixes SAPUI5.EnabledTOBehavior._properties
 * @mixes SAPUI5.CheckedTOBehavior._properties
 * @mixes WaitUntilEnabledBehavior._methods
 */
function CheckBoxTO(session, description) {
    description = description || CheckBox();
    var checkBoxTO = new SAPUI5BaseTestObject("SAPUICheckBox", session, description);

    checkBoxTO._mergeBehavior(Web.CheckBoxTOBehavior);
    checkBoxTO._mergeBehavior(HelperBehaviors.EnabledTOBehavior);
    checkBoxTO._mergeBehavior(HelperBehaviors.CheckedTOBehavior);

    return checkBoxTO;
}

module.exports.CheckBox = CheckBox;
module.exports.CheckBoxTO = CheckBoxTO;
