var Core = require("leanft.sdk.core");
var ObjectUtils = Core.ObjectUtils;
var SAPUI5BaseDesc = require("./sapui5_base.js").SAPUI5BaseDesc;
var SAPUI5BaseTestObject = require("./sapui5_base.js").SAPUI5BaseTestObject;
var HelperBehaviors = require("./helper_behaviors.js");

/**
 * The description of an SAPUI5 Header test object.
 * @param {Object} [props] Initializes a Header test object using the supplied property bag.
 * @returns {Header} the description object that was initialized.
 * @constructor
 * @memberof SAPUI5
 * @extends SAPUI5.SAPUI5BaseDesc
 * @mixes SAPUI5.TextDescriptionBehavior
 */
function Header(props) {
    var headerDes = new SAPUI5BaseDesc("Header");

    ObjectUtils.extend(headerDes, HelperBehaviors.TextDescriptionBehavior);

    headerDes._defineByPropertyBag(props);

    return headerDes;
}

/**
 * An SAPUI5 Header.
 * @constructor
 * @memberof SAPUI5
 * @class HeaderTO
 * @extends SAPUI5.SAPUI5BaseTestObject
 * @mixes SAPUI5.TextTOBehavior._properties
 */
function HeaderTO(session, description) {
    description = description || Header();
    var headerTO = new SAPUI5BaseTestObject("SAPUIHeader", session, description);

    headerTO._mergeBehavior(HelperBehaviors.TextTOBehavior);

    return headerTO;
}

module.exports.Header = Header;
module.exports.HeaderTO = HeaderTO;


