var Core = require("leanft.sdk.core");
var ObjectUtils = Core.ObjectUtils;
var SAPUI5BaseDesc = require("./sapui5_base.js").SAPUI5BaseDesc;
var SAPUI5BaseTestObject = require("./sapui5_base.js").SAPUI5BaseTestObject;
var HelperBehaviors = require("./helper_behaviors.js");

/**
 * The description of an SAPUI5 Label test object.
 * @param {Object} [props] Initializes a Label test object using the supplied property bag.
 * @returns {Label} the description object that was initialized.
 * @constructor
 * @memberof SAPUI5
 * @extends SAPUI5.SAPUI5BaseDesc
 * @mixes SAPUI5.TextDescriptionBehavior
 */
function Label(props) {
    var labelDes = new SAPUI5BaseDesc("Label");

    ObjectUtils.extend(labelDes, HelperBehaviors.TextDescriptionBehavior);

    labelDes._defineByPropertyBag(props);

    return labelDes;
}

/**
 * An SAPUI5 Label.
 * @constructor
 * @memberof SAPUI5
 * @class LabelTO
 * @extends SAPUI5.SAPUI5BaseTestObject
 * @mixes SAPUI5.TextTOBehavior._properties
 */
function LabelTO(session, description) {
    description = description || Label();
    var labelTO = new SAPUI5BaseTestObject("SAPUILabel", session, description);

    labelTO._mergeBehavior(HelperBehaviors.TextTOBehavior);

    return labelTO;
}

module.exports.Label = Label;
module.exports.LabelTO = LabelTO;