var Core = require("leanft.sdk.core");
var SAPUI5BaseDesc = require("./sapui5_base.js").SAPUI5BaseDesc;
var SAPUI5BaseTestObject = require("./sapui5_base.js").SAPUI5BaseTestObject;
var HelperBehaviors = require("./helper_behaviors.js");

/**
 * The description of an SAPUI5 Menubar test object.
 * @param {Object} [props] Initializes a Menubar test object using the supplied property bag.
 * @returns {Menubar} the description object that was initialized.
 * @constructor
 * @memberof SAPUI5
 * @extends SAPUI5.SAPUI5BaseDesc
 * @mixes SAPUI5.EnabledDescriptionBehavior
 * @mixes SAPUI5.ItemsCountDescriptionBehavior
 * @mixes SAPUI5.ItemsDescriptionBehavior
 */
function Menubar(props) {
    var menubarDes = new SAPUI5BaseDesc("Menubar");

    Core.ObjectUtils.extend(menubarDes, HelperBehaviors.EnabledDescriptionBehavior);
    Core.ObjectUtils.extend(menubarDes, HelperBehaviors.ItemsCountDescriptionBehavior);
    Core.ObjectUtils.extend(menubarDes, HelperBehaviors.ItemsDescriptionBehavior);

    menubarDes._defineByPropertyBag(props);

    return menubarDes;
}

/**
 * An SAPUI5 Menubar.
 * @constructor
 * @memberof SAPUI5
 * @class MenubarTO
 * @extends SAPUI5.SAPUI5BaseTestObject
 * @mixes SAPUI5.EnabledTOBehavior._properties
 * @mixes WaitUntilEnabledBehavior._methods
 * @mixes SAPUI5.ItemsTOBehavior._properties
 * @mixes SAPUI5.SelectItemTOBehavior._methods
 */
function MenubarTO(session, description) {
    description = description || Menubar();
    var menubarTO = new SAPUI5BaseTestObject("SAPUIMenubar", session, description);

    menubarTO._mergeBehavior(HelperBehaviors.EnabledTOBehavior);
    menubarTO._mergeBehavior(HelperBehaviors.ItemsTOBehavior);
    menubarTO._mergeBehavior(HelperBehaviors.SelectItemTOBehavior);

    return menubarTO;
}

module.exports.Menubar = Menubar;
module.exports.MenubarTO = MenubarTO;

