var Core = require("leanft.sdk.core");
var ObjectUtils = Core.ObjectUtils;
var Web = require("leanft.sdk.web").Behaviors;
var SAPUI5BaseDesc = require("./sapui5_base.js").SAPUI5BaseDesc;
var SAPUI5BaseTestObject = require("./sapui5_base.js").SAPUI5BaseTestObject;
var HelperBehaviors = require("./helper_behaviors.js");
var EditFieldTOBehavior = require("./edit_field").EditFieldTOBehavior;

/**
 * The description of an SAPUI5 Password test object.
 * @param {Object} [props] Initializes a Password test object using the supplied property bag.
 * @returns {Password} the description object that was initialized.
 * @constructor
 * @memberof SAPUI5
 * @extends SAPUI5.SAPUI5BaseDesc
 * @mixes Web.FormFieldBehavior
 * @mixes Web.EditFieldBaseDescriptionBehavior
 * @mixes Web.EditDescriptionBehavior
 * @mixes SAPUI5.EnabledDescriptionBehavior
 */
function Password(props) {
    var passwordDes = new SAPUI5BaseDesc("Password");

    ObjectUtils.extend(passwordDes, Web.FormFieldBehavior);
    ObjectUtils.extend(passwordDes, Web.EditFieldBaseDescriptionBehavior);
    ObjectUtils.extend(passwordDes, Web.EditDescriptionBehavior);
    ObjectUtils.extend(passwordDes, HelperBehaviors.EnabledDescriptionBehavior);

    passwordDes._defineByPropertyBag(props);

    return passwordDes;
}

/**
 * An SAPUI5 Password.
 * @constructor
 * @memberof SAPUI5
 * @class PasswordTO
 * @extends SAPUI5.SAPUI5BaseTestObject
 * @mixes Web.EditFieldBaseTestObjectBehavior._properties
 * @mixes Web.EditFieldBaseTestObjectBehavior._methods
 * @mixes Web.EditTOBehavior._properties
 * @mixes Web.EditTOBehavior._methods
 * @mixes SAPUI5.EnabledTOBehavior._properties
 * @mixes WaitUntilEnabledBehavior._methods
 * @mixes SAPUI5.ReadOnlyTOBehavior._properties
 * @mixes SAPUI5.EditFieldTOBehavior._methods
 */
function PasswordTO(session, description) {
    description = description || Password();
    var passwordTO = new SAPUI5BaseTestObject("SAPUIPassword", session, description);

    passwordTO._mergeBehavior(Web.EditFieldBaseTestObjectBehavior);
    passwordTO._mergeBehavior(Web.EditTOBehavior);
    passwordTO._mergeBehavior(HelperBehaviors.EnabledTOBehavior);
    passwordTO._mergeBehavior(HelperBehaviors.ReadOnlyTOBehavior);
    passwordTO._mergeBehavior(EditFieldTOBehavior);

    return passwordTO;
}

module.exports.Password = Password;
module.exports.PasswordTO = PasswordTO;

