var Core = require("leanft.sdk.core");
var ObjectUtils = Core.ObjectUtils;
var Web = require("leanft.sdk.web").Behaviors;
var SAPUI5BaseDesc = require("./sapui5_base.js").SAPUI5BaseDesc;
var SAPUI5BaseTestObject = require("./sapui5_base.js").SAPUI5BaseTestObject;
var HelperBehaviors = require("./helper_behaviors.js");

/**
 * The description of an SAPUI5 RadioButton test object.
 * @param {Object} [props] Initializes a RadioButton test object using the supplied property bag.
 * @returns {RadioButton} the description object that was initialized.
 * @constructor
 * @memberof SAPUI5
 * @extends SAPUI5.SAPUI5BaseDesc
 * @mixes Web.ButtonDescriptionBehavior
 * @mixes SAPUI5.CheckedDescriptionBehavior
 * @mixes SAPUI5.ReadOnlyDescriptionBehavior
 * @mixes SAPUI5.EnabledDescriptionBehavior
 */
function RadioButton(props) {
    var radioButtonDes = new SAPUI5BaseDesc("RadioButton");

    ObjectUtils.extend(radioButtonDes, Web.ButtonDescriptionBehavior);
    ObjectUtils.extend(radioButtonDes, HelperBehaviors.CheckedDescriptionBehavior);
    ObjectUtils.extend(radioButtonDes, HelperBehaviors.ReadOnlyDescriptionBehavior);
    ObjectUtils.extend(radioButtonDes, HelperBehaviors.EnabledDescriptionBehavior);

    radioButtonDes._defineByPropertyBag(props);

    return radioButtonDes;
}

/**
 * An SAPUI5 RadioButton.
 * @constructor
 * @memberof SAPUI5
 * @class RadioButtonTO
 * @extends SAPUI5.SAPUI5BaseTestObject
 * @mixes Web.ButtonTOBehavior._properties
 * @mixes SAPUI5.EnabledTOBehavior._properties
 * @mixes WaitUntilEnabledBehavior._methods
 * @mixes SAPUI5.ReadOnlyTOBehavior._properties
 * @mixes SAPUI5.CheckedTOBehavior._properties
 * @mixes SAPUI5.RadioButtonTOBehavior._methods
 */
function RadioButtonTO(session, description) {
    description = description || RadioButton();
    var radioButtonTO = new SAPUI5BaseTestObject("SAPUIRadioButton", session, description);

    radioButtonTO._mergeBehavior(Web.ButtonTOBehavior);
    radioButtonTO._mergeBehavior(HelperBehaviors.EnabledTOBehavior);
    radioButtonTO._mergeBehavior(HelperBehaviors.ReadOnlyTOBehavior);
    radioButtonTO._mergeBehavior(HelperBehaviors.CheckedTOBehavior);
    radioButtonTO._mergeBehavior(RadioButtonTOBehavior);

    return radioButtonTO;
}

//Consts
var setWiredName = "Select";
var activateWiredName = "Activate";

/**
 * @ignore
 * @memberof SAPUI5
 */
var RadioButtonTOBehavior = {
    /**
     * @ignore
     * @memberof SAPUI5.RadioButtonTOBehavior
     */
    _methods: {
        /**
         * Selects the radio button.
         * @function
         * @returns {Promise<void>} a promise that is fulfilled after selecting the radio button.
         */
        set: {
            wiredName: setWiredName,
            exec: function () {
                return this._executeWithEvents(arguments, setWiredName);
            }
        },
        /**
         * Activates the radio button.
         * @function
         * @returns {Promise<void>} a promise that is fulfilled after the activate operation has occurred.
         */
        activate: {
            wiredName: activateWiredName,
            exec: function () {
                return this._executeWithEvents(arguments, activateWiredName);
            }
        }
    }
};

module.exports.RadioButton = RadioButton;
module.exports.RadioButtonTO = RadioButtonTO;
