var Core = require("leanft.sdk.core");
var WebBehaviors = require("leanft.sdk.web").Behaviors;

/**
 * SAPUI5 namespace
 * @namespace SAPUI5
 */


/**
 * The base description for all SAPUI5 elements
 * @class SAPUI5BaseDesc
 * @extends Web.WebBaseDescription
 * @mixes Web.WebElementDescriptionBehavior
 * @memberof SAPUI5
 * @ignore
 */
function SAPUI5BaseDesc(leanFtType) {
    var desc = new WebBehaviors.WebBaseDescription(leanFtType);
    desc.leanFTType.technology = "SAPUI5";
    Core.ObjectUtils.extend(desc, WebBehaviors.WebElementDescriptionBehavior);

    return desc;
}

/**
 * @class SAPUI5BaseTestObject
 * @param nativeClass
 * @param session
 * @param description
 * @constructor
 * @extends Web.WebBaseTestObject
 * @mixes ClickBehavior._methods
 * @mixes DragAndDropBehavior._methods
 * @mixes Web.WebElementTOBehavior._properties
 * @mixes Web.WebElementTOBehavior._methods
 * @abstract
 * @memberof SAPUI5
 * @ignore
 */
function SAPUI5BaseTestObject(nativeClass,session,description) {
    var testObj = new WebBehaviors.WebBaseTestObject(nativeClass, session, description);

    testObj._mergeBehavior(Core.ClickBehavior);
    testObj._mergeBehavior(Core.DragAndDropBehavior);
    testObj._mergeBehavior(WebBehaviors.WebElementTOBehavior);

    return testObj;
}

module.exports.SAPUI5BaseDesc = SAPUI5BaseDesc;
module.exports.SAPUI5BaseTestObject = SAPUI5BaseTestObject;