var Core = require("leanft.sdk.core");
var ObjectUtils = Core.ObjectUtils;
var Web = require("leanft.sdk.web").Behaviors;
var SAPUI5BaseDesc = require("./sapui5_base.js").SAPUI5BaseDesc;
var SAPUI5BaseTestObject = require("./sapui5_base.js").SAPUI5BaseTestObject;
var HelperBehaviors = require("./helper_behaviors.js");

/**
 * The description of an SAPUI5 Toggle Button test object.
 * @param {Object} [props] Initializes a Toggle Button test object using the supplied property bag.
 * @returns {Toggle} the description object that was initialized.
 * @constructor
 * @memberof SAPUI5
 * @extends SAPUI5.SAPUI5BaseDesc
 * @mixes Web.FormFieldBehavior
 * @mixes Web.ButtonDescriptionBehavior
 * @mixes SAPUI5.EnabledDescriptionBehavior
 * @mixes SAPUI5.CheckedDescriptionBehavior
 */
function Toggle(props) {
    var toggleDes = new SAPUI5BaseDesc("ToggleButton");

    ObjectUtils.extend(toggleDes, Web.ButtonDescriptionBehavior);
    ObjectUtils.extend(toggleDes, HelperBehaviors.EnabledDescriptionBehavior);
    ObjectUtils.extend(toggleDes, HelperBehaviors.CheckedDescriptionBehavior);

    toggleDes._defineByPropertyBag(props);

    return toggleDes;
}

/**
 * An SAPUI5 Toggle Button.
 * @constructor
 * @memberof SAPUI5
 * @class ToggleTO
 * @extends SAPUI5.SAPUI5BaseTestObject
 * @mixes Web.ButtonTOBehavior._properties
 * @mixes Web.CheckBoxTOBehavior._properties
 * @mixes Web.CheckBoxTOBehavior._methods
 * @mixes SAPUI5.CheckedTOBehavior._properties
 * @mixes SAPUI5.EnabledTOBehavior._properties
 * @mixes WaitUntilEnabledBehavior._methods
 */
function ToggleTO(session, description) {
    description = description || Toggle();
    var toggleTO = new SAPUI5BaseTestObject("SAPUIToggleButton", session, description);

    toggleTO._mergeBehavior(Web.ButtonTOBehavior);
    toggleTO._mergeBehavior(Web.CheckBoxTOBehavior);
    toggleTO._mergeBehavior(HelperBehaviors.CheckedTOBehavior);
    toggleTO._mergeBehavior(HelperBehaviors.EnabledTOBehavior);

    return toggleTO;
}

module.exports.Toggle = Toggle;
module.exports.ToggleTO = ToggleTO;
