var assert = require("assert");
var sinon = require("sinon");
var Web = require("leanft.sdk.web");

describe("Password Description unit tests", function() {
    var PasswordDesc = require("../lib/password.js").Password;
    var passwordDescUnderTest;

    beforeEach(function() {
        passwordDescUnderTest = new PasswordDesc();
    });

    it("should have all keys of Web.Edit", function() {
        var WebEditDesc = Web.Edit;
        var webEditDesc = new WebEditDesc();

        for(var webKey in webEditDesc) {
            assert(webKey in passwordDescUnderTest, "key: " + webKey + " is missing in Password");
        }
    });
});


describe("Password Test Object Behavior",function() {
    var PasswordTO = require("../lib/password.js").PasswordTO;
    var passwordTOBehaviorUnderTest;

    beforeEach(function () {
        passwordTOBehaviorUnderTest = PasswordTO({});
    });

    it("should have all keys of Web.Edit", function () {
        var WebEditTO = Web.EditTO;
        var webEditTO = new WebEditTO({});

        for (var webKey in webEditTO) {
            assert(webKey in passwordTOBehaviorUnderTest, "key: " + webKey + " is missing in Password");
        }
    });
});