var Core = require("leanft.sdk.core");
var Utils = Core.ObjectUtils;
var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;
var UiWithLayoutInfo = require("./ui_object_base_with_layout_info.js");
var UiObjectWithLayoutInfoBehaviour = UiWithLayoutInfo.UiObjectWithLayoutInfoBehaviour;
var UiObjectWithLayoutInfoDescription = UiWithLayoutInfo.UiObjectWithLayoutInfoDescription;

/**
 * The description of a Button test object.
 * @param {Object} [props] Initializes a Button test object using the supplied property bag.
 * @returns {Button} the description object that was initialized.
 * @class Button
 * @memberof StdWin
 * @extends StdWin.UiObjectBaseDescription
 * @mixes StdWin.UiObjectWithLayoutInfoDescription 
 */
function Button(props) {
    var buttonDesc = new UiObjectBaseDescription("Button");
    Utils.extend(buttonDesc, UiObjectWithLayoutInfoDescription);
    buttonDesc._defineByPropertyBag(props);
    return buttonDesc;
}

/**
 * A Button object
 * @constructor
 * @class ButtonTO
 * @extends StdWin.UiObjectBaseTO
 * @mixes StdWin.UiObjectWithLayoutInfoBehaviour._properties
 * @memberof StdWin
 */
function ButtonTO(session, description){
    description = description || Button();
    var buttonTO = new UiObjectBaseTO("WinButton", session, description);
    buttonTO._mergeBehavior(UiObjectWithLayoutInfoBehaviour);
    return buttonTO;
}

module.exports.Button = Button;
module.exports.ButtonTO = ButtonTO;
