var Core = require("leanft.sdk.core");
var Utils = Core.ObjectUtils;
var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;
var UiWithLayoutInfo = require("./ui_object_base_with_layout_info.js");
var UiObjectWithLayoutInfoBehaviour = UiWithLayoutInfo.UiObjectWithLayoutInfoBehaviour;
var UiObjectWithLayoutInfoDescription = UiWithLayoutInfo.UiObjectWithLayoutInfoDescription;
var WindowPackage = require("./window.js");
var WindowDescriptionBehavior = WindowPackage.WindowDescriptionBehavior;
var WindowTO = WindowPackage.WindowTO;
var WindowTOBehavior = WindowPackage.WindowTOBehavior;
var WindowObjectDescriptionBaseBehavior = Core.WindowObjectDescriptionBaseBehavior;
var WindowObjectBaseBehavior = Core.WindowObjectBaseBehavior;
var WindowObjectBaseExtendedBehavior = Core.WindowObjectBaseExtendedBehavior;
var WindowBasePropsBehavior = Core.WindowBasePropsBehavior;

/**
 * The description of a Dialog test object.
 * @param {Object} [props] Initializes a Dialog test object using the supplied property bag.
 * @returns {Dialog} the description object that was initialized.
 * @class Dialog
 * @memberof StdWin
 * @extends StdWin.UiObjectBaseDescription
 * @mixes WindowObjectDescriptionBaseBehavior
 * @mixes StdWin.UiObjectWithLayoutInfoDescription
 * @mixes StdWin.WindowDescriptionBehavior
 * @mixes StdWin.DialogDescriptionBehavior
 */
function Dialog(props) {
    var dialogDesc = new UiObjectBaseDescription("Dialog");
    Utils.extend(dialogDesc, WindowObjectDescriptionBaseBehavior);
    Utils.extend(dialogDesc, UiObjectWithLayoutInfoDescription);
    Utils.extend(dialogDesc, WindowDescriptionBehavior);
    dialogDesc._defineByPropertyBag(props);
    return dialogDesc;
}

/**
 * A Dialog object,
 * @constructor
 * @class DialogTO
 * @extends StdWin.UiObjectBaseTO
 * @mixes StdWin.UiObjectWithLayoutInfoBehaviour._properties
 * @mixes WindowObjectBaseBehavior._methods
 * @mixes WindowObjectBaseExtendedBehavior._methods
 * @mixes WindowObjectBaseExtendedBehavior._properties
 * @mixes WindowBasePropsBehavior._properties
 * @mixes StdWin.WindowTOBehavior._properties
 * @memberof StdWin
 */
function DialogTO(session, description) {
    description = description || Dialog();
    var dialogTO = new UiObjectBaseTO("Dialog", session, description);
    dialogTO._mergeBehavior(UiObjectWithLayoutInfoBehaviour);
    dialogTO._mergeBehavior(WindowObjectBaseBehavior);
    dialogTO._mergeBehavior(WindowObjectBaseExtendedBehavior);
    dialogTO._mergeBehavior(WindowBasePropsBehavior);
    dialogTO._mergeBehavior(WindowTOBehavior);
    return dialogTO;   
}

module.exports.Dialog = Dialog;
module.exports.DialogTO = DialogTO;
