var Core = require("leanft.sdk.core");
var EditorBaseTOBehavior = Core.EditorBaseTOBehavior;

var Utils = Core.ObjectUtils;
var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;
var UiWithLayoutInfo = require("./ui_object_base_with_layout_info.js");
var UiObjectWithLayoutInfoBehaviour = UiWithLayoutInfo.UiObjectWithLayoutInfoBehaviour;
var UiObjectWithLayoutInfoDescription = UiWithLayoutInfo.UiObjectWithLayoutInfoDescription;
var Convert = Core.Convert;

/**
 * The description of an Editor test object.
 * @param {Object} [props] Initializes an Editor test object using the supplied property bag.
 * @returns {Editor} the description object that was initialized.
 * @class Editor
 * @memberof StdWin
 * @extends StdWin.UiObjectBaseDescription
 * @mixes StdWin.UiObjectWithLayoutInfoDescription
 */
function Editor(props) {
    var editorDesc = new UiObjectBaseDescription("Editor");
    Utils.extend(editorDesc, UiObjectWithLayoutInfoDescription);
    editorDesc._defineByPropertyBag(props);
    return editorDesc;
}

/**
 * An Editor object
 * @constructor
 * @class EditorTO
 * @extends StdWin.UiObjectBaseTO
 * @mixes StdWin.UiObjectWithLayoutInfoBehaviour._properties
 * @mixes EditorBaseTOBehavior._properties
 * @mixes EditorBaseTOBehavior._methods
 * @memberof StdWin
 */
function EditorTO(session, description) {
    description = description || Editor();
    var editorTO = new UiObjectBaseTO("WinEditor", session, description);
    editorTO._mergeBehavior(UiObjectWithLayoutInfoBehaviour);
    editorTO._mergeBehavior(EditorBaseTOBehavior);
    return editorTO;
}

module.exports.Editor = Editor;
module.exports.EditorTO = EditorTO;
